mapper.select:
SELECT DISTINCT n_note_task.id, n_note_task.task_name, n_note_task.start_time, n_note_task.end_time, n_note_task.task_describe, n_note_task.knowledge_base_id, n_note_task.status, n_note_task_submission_record.note_id, n_note_task_submission_record.submit_time, sys_user.username as creator_username, sys_user.nickname as creator_nickname, n_note_task.update_time, n_user_note_task.status as submission_status FROM n_note_task LEFT JOIN n_note_task_submission_record ON (n_note_task.id = n_note_task_submission_record.note_task_id AND n_note_task_submission_record.user_id = #{submitUserId} AND n_note_task_submission_record.status = 0) LEFT JOIN n_user_note_task ON (n_note_task.id = n_user_note_task.note_task_id AND n_user_note_task.user_id = #{submitUserId}) LEFT JOIN sys_user ON n_note_task.create_by = sys_user.id WHERE n_note_task.is_delete = 0
AND n_note_task.id = #{noteTaskId}
AND n_note_task.knowledge_base_id = #{id}

SELECT sys_user.username, sys_user.nickname, n_note_task.task_name, n_knowledge_base.name as knowledge_base_name, note_task_id, n_note_task_submission_record.note_id, COUNT(*) as edit_count FROM n_note_task_submission_record LEFT JOIN n_note_task ON n_note_task_submission_record.note_task_id = n_note_task.id LEFT JOIN n_knowledge_base ON n_note_task.knowledge_base_id = n_knowledge_base.id LEFT JOIN n_note_history ON n_note_task_submission_record.note_id = n_note_history.note_id LEFT JOIN sys_user ON n_note_task_submission_record.user_id = sys_user.id LEFT JOIN n_note ON n_note.id = n_note_task_submission_record.note_id WHERE n_knowledge_base.id = #{knowledgeBaseId}
AND sys_user.id = #{userId}
GROUP BY n_note_task_submission_record.user_id, note_task_id, n_note_task_submission_record.note_id, n_knowledge_base.name, sys_user.nickname, sys_user.username, n_note_task.task_name, n_knowledge_base.id
