mapper.select:
SELECT n_note_task_submission_record.note_task_id, n_note_task_submission_record.user_id, n_note_task_submission_record.note_id, n_note_task_submission_record.submit_time, n_note_task_submission_record.is_delete, n_note_task_submission_record.create_by, n_note_task_submission_record.create_time, n_note_task_submission_record.update_by, n_note_task_submission_record.update_time, n_note_task_submission_record.remark, sys_user.username, sys_user.nickname, n_note.title as note_title FROM n_note_task_submission_record LEFT JOIN n_note ON n_note_task_submission_record.note_id = n_note.id LEFT JOIN sys_user ON n_note_task_submission_record.user_id = sys_user.id WHERE n_note_task_submission_record.is_delete = 0
AND n_note_task_submission_record.user_id = #{userId}
AND n_note_task_submission_record.note_task_id = #{noteTaskId}
AND n_note_task_submission_record.note_id = #{noteId}
